INSERT INTO `app01_partjobinfo` VALUES (19, '北京', '全市', '市场销售', '学历不限', 'http://bj.ssjzw.com/job/2012142882.html', '线上兼职销售东北特色农产品', 452, '100元/天', '2020-10-24 09:49:50.000000', '', '[\'【工作薪资】待遇优厚，工资，支持日结可以十天一结也可以月结。\', \'【工作内容】负责通过朋友圈，微信群或者其他渠道推广公司特色农产品，主要为五常大米。小程序下单，可后台直接查询提成，提现等。\', \'【上班时间】时间自由，随意。\', \'【工作地点】全国各地均可，线上销售。\', \'【工作要求】\', \'只要你有微信，有朋友圈，想赚钱，请联系我!\', \'无任何押金。\', \'【其他福利】如果你有团队，根据业绩，每个月奖励可达500-元。\']', 12.00);
