INSERT INTO `app01_partjobinfo` VALUES (174, '天津', '蓟县', '市场销售', '高中学生学历不限', 'http://tj.ssjzw.com/job/2012147841.html', '健身教练助理', 403, '40元/小时', '2021-03-12 10:14:00.000000', '赛普力量教育科技有限公司  ', '[\'【适合人群】\', \'1、高中及以上学历，17至35周岁。\', \'2、对健身运动感兴趣的人士\', \'3、想要转行，学习一门技术能够长期发展;\', \'4、刚毕业的大学生，找不到合适工作，迷茫的;\', \'5、房贷车贷结婚生子压力大的;想挑战高薪的人士;\', \'6、在车间或者工厂重复枯燥工作，每月拿着微薄薪资;\', \'7、辍学在家，学历不高，迫切想要一份好工作的;\', \'岗位职责：\', \'1、负责区域的市场开发/客户维护和销售管理等工作；\', \'2、制定自己的销售计划、并按计划拜访客户和开发新客户；\', \'3、向有装修意向的客户提供家装建议与规划，推广及销售公司产品，完成销售的任务目标；\', \'4、邀约有装修意向的客户到店与设计师洽谈，协助设计师完成签单。\', \'5、协助销售主管制定销售策略、销售计划，以及量化销售目标；\', \'6、接待来访客户，以及综合协调日常销售事务。\', \'任职要求：\', \'1、18-40周岁，性别不限，学历不限；\', \'2、熟悉家装家居行业。\', \'3、1年及以上销售工作经验，有销售经验者优先；\', \'4、精通销售技巧，熟悉互联网络，熟练使用网络交流工具；\', \'5、具备一定的市场分析及判断能力，良好的客户服务意识；\', \'6、反应敏捷、表达能力强，具有较强的沟通能力及交际技巧，具有亲和力；\', \'7、有吃苦耐劳的精神，敢于挑战高薪。\']', 40.00);
