INSERT INTO `app01_partjobinfo` VALUES (360, '天津', '南开', '家教', '学生', 'http://tj.ssjzw.com/job/2012140218.html', '招辅导老师', 387, '18元/小时', '2020-09-10 09:26:11.000000', '艺久久舞文化艺术有限责任公司 ', '[\'【工作内容】\', \'1、负责所带班级学生的日常管理工作；\', \'2、就学生的学习情况与家长保持必要沟通，提高家长的满意度。\', \'【工作要求】\', \'1、普通话标准，语言表达能力强；\', \'2、有强烈责任感，良好的职业操守，诚信守时；\', \'3、熟悉小学各科教学内容，能够独立完成小学生晚托答疑；\']', 18.00);
INSERT INTO `app01_partjobinfo` VALUES (361, '天津', '南开', '培训', '学生', 'http://tj.ssjzw.com/job/2012140465.html', '招聘专兼职学生课业辅导', 304, '100元/天', '2020-09-13 10:14:11.000000', '盈优加教育信息咨询 ', '[\'主要负责学生课业辅导，辅助其他岗位教室工作\']', 12.00);
INSERT INTO `app01_partjobinfo` VALUES (362, '天津', '南开', '培训', '学历不限', 'http://tj.ssjzw.com/job/2012100192.html', '高薪招聘私人教练', 852, '8000元/月', '2019-04-20 10:26:38.000000', '北京加州阳光国际健身俱乐部有限公司 ', '[\'高薪招聘\', \'壹健身王府壹号店，私教预售\', \'对已办卡客户进行私教销售，维护，上课\']', 45.00);
INSERT INTO `app01_partjobinfo` VALUES (363, '天津', '南开', '培训', '学历不限', 'http://tj.ssjzw.com/job/2012102239.html', '招聘周末兼职艺术老师', 980, '150元/小时', '2019-05-02 15:17:18.000000', '', '[\'有艺术专长，例如舞蹈、音乐、绘画、国学、编程、右脑等，周六日早九晚六，薪资待遇高，管吃，奖金丰厚\']', 150.00);
INSERT INTO `app01_partjobinfo` VALUES (364, '天津', '南开', '培训', '学历不限', 'http://tj.ssjzw.com/job/2012145823.html', '零食店店员临时工', 395, '50元/小时', '2020-12-21 10:51:21.000000', '领豚信息技术有限公司 ', '[\'【工作时间】\', \'早班9：50-19：00，中班13：00-22：00，吃饭休息1小时不计算工资\', \'【工作内容】\', \'负责品牌折扣商品（包括零食，衣服，包包等）的理货盘点陈列等工作\', \'【个人要求】\', \'男女不限，年龄18-35岁，无明显纹身。工期：12月10至3月10日，至少做一个月，能做完工期的优先\', \'【薪资待遇】\', \'18元/小时，每周五结算上一周工资\']', 50.00);
